import React, { useState } from 'react';

function App() {
  const [inputValue, setInputValue] = useState(''); // テキストボックスの値を保持するstate

  // テキストボックスの値が変更された際のハンドラ
  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  // ボタンがクリックされた際のハンドラ
  const handleClick = () => {
    alert(`入力されたテキスト: ${inputValue}`);
  };

  return (
    <div className="App">
      <h1>テキストボックスとボタンの例</h1>
      <input type="text" placeholder="ここにテキストを入力" value={inputValue} onChange={handleInputChange} />
      <button onClick={handleClick}>表示</button>
    </div>
  );
}

export default App;
